import React, { Component } from 'react'
import NewsItem from '../NewsItem'

export class News extends Component {
    
    constructor(){
        super();
        this.state={
           articles: [],
           loading: true,
           page: 1
        }
    }
    async componentDidMount(){
        console.log("cdm");
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c";
        let data = await fetch(url);
        let parseData = await data.json();
        this.setState({articles: parseData.articles})
    }

    handlePrevClick = async ()=>{
        console.log("previous")

        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c&page=${this.state.page - 1 }`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({
            page: this.state.page - 1,
            articles: parseData.articles
        })
    }

    handleNextClick = async ()=>{
        console.log("Next")

        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c&page=${this.state.page + 1 }`;
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({
            page: this.state.page + 1,
            articles: parseData.articles
        })
    }

    render() {
        return (
            <div className='container my-3'>
                <h2>NewsMonkey - Top Headlines</h2>
               <div className="row my-3">
                   {this.state.articles.map((element)=>{
                       return <div className="col-md-4" key={element.url}>
                       <NewsItem title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage} newsUrl={element.url} />
                   </div>
                   })}
                </div>
                <div className="container my-3 d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePrevClick}> &larr;Previous</button>
                <button type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div> 
            </div>
        )
    }
}

export default News
