import React, { Component } from 'react'
import NewsItem from '../NewsItem'
import Spinner from './Spinner';

export class News extends Component {
    
    constructor(){
        super();
        this.state={
           articles: [],
           loading: false,
           page: 1
        }
    }
    async componentDidMount(){
        console.log("cdm");
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c&page=1&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parseData = await data.json();
        this.setState({articles: parseData.articles, totalResults: parseData.totalResults, loading: false})
    }

    handlePrevClick = async ()=>{
        console.log("previous")

        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c&page=${this.state.page - 1 }&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parseData = await data.json();

        this.setState({
            page: this.state.page - 1,
            articles: parseData.articles,
            loading: false
        })
    }

    handleNextClick = async ()=>{
        console.log("Next")
        if(!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
            let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3002be1ebbb04ef58159f9c0101e315c&page=${this.state.page + 1 }&pageSize=${this.props.pageSize}`;
            this.setState({loading: true});
            let data = await fetch(url);
            let parseData = await data.json();

            this.setState({
                page: this.state.page + 1,
                articles: parseData.articles,
                loading: false
            })
        }
    }

    render() {
        return (
            <div className='container my-3'>
                <h1 className="text-center my-2">NewsMonkey - Top Headlines</h1>
                {this.state.loading && <Spinner />}
               <div className="row my-3">
                   {this.state.articles.map((element)=>{
                       return <div className="col-md-4" key={element.url}>
                       <NewsItem title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage} newsUrl={element.url} />
                   </div>
                   })}
                </div>
                <div className="container my-3 d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr;Previous</button>
                <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div> 
            </div>
        )
    }
}

export default News
